    private void Boton_EntrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Boton_EntrarMouseClicked
        String Nombre_Usuario_1 = Nom_1.getText();
        String Nombre_Usuario_2 = Nom_2.getText();

        String Error_Nombres_Usuarios = Validar_Nombres_Usuarios(Nombre_Usuario_1, Nombre_Usuario_2);
        Almacen_Nombres Almacen_Nombres = new Almacen_Nombres();
        Almacen_Nombres.setNombres(Nombre_Usuario_1, Nombre_Usuario_2);

        if (Error_Nombres_Usuarios != null) {
            // Mostrar mensaje de error en el panel
            Mensaje_Error.setText(Error_Nombres_Usuarios);
            Mensaje_Error.setVisible(true);
            // Crear un temporizador para ocultar el mensaje despu√©s de un segundo
            Timer timer = new Timer(1000, new ActionListener() {
                @Override
                public void actionPerformed(ActionEvent e) {
                    Mensaje_Error.setVisible(false);
                }
            });
            timer.setRepeats(false);
            timer.start();
        } else {
            Jugador_1 Jugador_1 = new Jugador_1();
            Panel_Principal.ShowPanel(Jugador_1);
        }
    }//GEN-LAST:event_Boton_EntrarMouseClicked


